const INITIAL_STATE = {
    countries: [],
    message: "ERROR"
}

export const reducer = (state = INITIAL_STATE, action) => {
    switch (action.type) {
        case "GET_COUNTRIES_SUCCESS":
            return {...state, countries: action.payload}
        case "GET_COUNTRIES_ERROR":
            return state.message
        default:
            return state;
    }
}
